wal_train |&gt;
  mutate(year = year(date)) |&gt;
  count(year)

# A tibble: 3 × 2
   year       n
  &lt;dbl&gt;   &lt;int&gt;
1  2012 1806303
2  2013 1559439
3  2014 1251858

wal_2012 &lt;- wal_train |&gt;
  mutate(year = year(date)) |&gt;
  filter(year == 2012)

wal_2012 |&gt;
  group_by(store_nbr) |&gt;
  summarise(total = sum(units)) |&gt;
  mutate(store_nbr = fct_reorder(factor(store_nbr), total)) |&gt; 
  ggplot(aes(total, store_nbr)) +
  geom_col() +
  scale_x_continuous(labels = scales::comma_format())







wal_train |&gt;
  group_by(store_nbr) |&gt;
  summarise(total = sum(units)) |&gt;
  mutate(store_nbr = fct_reorder(factor(store_nbr), total)) |&gt; 
  ggplot(aes(total, store_nbr)) +
  geom_col() +
  scale_x_continuous(labels = scales::comma_format())







wal_train |&gt; 
  mutate(year = year(date)) |&gt;
  group_by(store_nbr, year) |&gt;
  summarise(total = sum(units), .groups = "drop") |&gt;
  mutate(store_nbr = reorder_within(factor(store_nbr), total, year)) |&gt; 
  ggplot(aes(total, store_nbr)) +
  geom_col() +
  scale_y_reordered() +
  scale_x_continuous(labels = scales::comma_format()) +
  facet_wrap(~year, scales = "free")







wal_train |&gt; 
  mutate(year = year(date),
         month = month(date, label = TRUE)) |&gt;
  group_by(store_nbr, year, month) |&gt;
  summarise(total = sum(units), .groups = "drop") |&gt;
  mutate(store_nbr = reorder_within(factor(store_nbr), total, month)) |&gt; 
  ggplot(aes(total, store_nbr)) +
  geom_col() +
  scale_y_reordered() +
  scale_x_continuous(labels = scales::comma_format()) +
  facet_wrap(~month, scales = "free_y", nrow = 2)









items

wal_train2 &lt;- wal_train |&gt; 
  mutate(store_nbr = factor(store_nbr),
         item_nbr = factor(item_nbr),
         year = year(date),
         month = month(date, label = TRUE))


wal_train2 |&gt; 
  group_by(item_nbr, year) |&gt; 
  summarise(total_item = sum(units)) |&gt;
  mutate(item_nbr = fct_reorder(item_nbr, total_item)) |&gt; 
  ggplot(aes(total_item, item_nbr)) +
  geom_col() +
  scale_x_log10() 

`summarise()` has grouped output by 'item_nbr'. You can override using the
`.groups` argument.


Warning in scale_x_log10(): log-10 transformation introduced infinite values.


Warning: Removed 30 rows containing missing values or values outside the scale range
(`geom_col()`).








wal_train2 |&gt; 
  group_by(item_nbr, year) |&gt; 
  summarise(total_item = sum(units), .groups = "drop") |&gt;
  mutate(item_nbr = reorder_within(item_nbr, total_item, year)) |&gt; 
  ggplot(aes(total_item, item_nbr)) +
  geom_col() +
  scale_y_reordered() +
  scale_x_log10() +
  facet_wrap(~year, scales = "free")

Warning in scale_x_log10(): log-10 transformation introduced infinite values.
Removed 30 rows containing missing values or values outside the scale range
(`geom_col()`).








wal_train2 |&gt; 
  group_by(item_nbr, year) |&gt; 
  summarise(total_item = sum(units), .groups = "drop") |&gt;
  ggplot() +
  geom_line(aes(year, total_item, colour = item_nbr), show.legend = F) +
  scale_x_continuous(labels = seq(2012, 2014, 1), breaks = seq(2012, 2014, 1)) +
  scale_y_continuous(labels = scales::comma_format())







wal_train2 |&gt; 
  group_by(item_nbr, month, year) |&gt; 
  summarise(total_item = sum(units), .groups = "drop") |&gt; 
  ggplot() +
  geom_line(aes(month, total_item, colour = item_nbr, group = item_nbr), show.legend = F) +
  scale_y_continuous(labels = scales::comma_format()) +
  facet_wrap(~year)







wal_train2 |&gt; 
  unite(col = "date2", month:year,  sep = "-", remove = FALSE) |&gt; 
  mutate(date2 = as.Date(paste0("01-", date2), format = "%d-%b-%Y")) |&gt; 
  group_by(item_nbr, date2) |&gt; 
  summarise(total_item = sum(units), .groups = "drop") |&gt; 
  ggplot() +
  geom_line(aes(date2, total_item, colour = item_nbr, group = item_nbr), show.legend = F) +
  scale_x_date(guide = guide_axis(angle = 30), date_labels = "%b-%Y", date_breaks = "1 month") +
  scale_y_continuous(labels = scales::comma_format()) 









# 2012 data

weather_store &lt;- read_csv("C:/Users/dell/Desktop/walmart/weather_store.csv")

Rows: 1806303 Columns: 24
── Column specification ────────────────────────────────────────────────────────
Delimiter: ","
chr  (18): tmax, tmin, tavg, depart, dewpoint, wetbulb, heat, cool, sunrise,...
dbl   (5): store_nbr, item_nbr, units, year, station_nbr
date  (1): date

ℹ Use `spec()` to retrieve the full column specification for this data.
ℹ Specify the column types or set `show_col_types = FALSE` to quiet this message.

walmart &lt;-
  wal_train |&gt;
  mutate(year = year(date)) |&gt;
  filter(units &gt; 0)|&gt; 
  left_join(key) |&gt; 
  left_join(weather) |&gt; 
  mutate(tavg2 = suppressWarnings(as.numeric(tavg)),
         preciptotal = suppressWarnings(as.numeric(preciptotal))) |&gt;
  filter(!is.na(tavg2))|&gt; 
  filter(!is.na(preciptotal))

Joining with `by = join_by(store_nbr)`
Joining with `by = join_by(date, station_nbr)`

walmart2 &lt;-
  wal_train |&gt;
  mutate(year = year(date)) |&gt;
  filter(units &gt; 0)|&gt; 
  left_join(key) |&gt; 
  left_join(weather) |&gt; 
  mutate(tavg2 = suppressWarnings(as.numeric(tavg)),
         preciptotal = suppressWarnings(as.numeric(preciptotal))) |&gt;
  filter(!is.na(tavg2))|&gt; 
  filter(!is.na(preciptotal)) |&gt; 
  filter(!is.na(codesum)) |&gt;
  separate_longer_delim(codesum, delim = " ") |&gt; 
  mutate(codesum = str_extract_all(codesum, ".{2}")) |&gt;
  unnest(codesum)

Joining with `by = join_by(store_nbr)`
Joining with `by = join_by(date, station_nbr)`

walmart2 |&gt; 
  filter(units &lt; 2000) |&gt;
  ggplot(aes(units)) +
  geom_histogram() 

